define(function(require,exports,module){"use strict";var Menus=brackets.getModule("command/Menus"),Resizer=brackets.getModule("utils/Resizer"),UrlParams=brackets.getModule("utils/UrlParams").UrlParams;function hide(){if(shouldHideUI()){removeMainToolBar();removeLeftSideToolBar();removeRightSideToolBar();removeTitleBar()}}function shouldHideUI(){var params=new UrlParams;params.parse();return params.get("ui")!=="1"}function removeLeftSideToolBar(){$("#working-set-list-second-pane").css({visibility:"hidden"});$("#sidebar .working-set-splitview-btn").remove();Resizer.hide("#sidebar")}function removeTitleBar(){$("#titlebar").remove();$("#first-pane .pane-header").remove();$("#editor-holder").css({height:"96%"});$("#first-pane .pane-content, .cm-s-light-theme").css({height:"100%"})}function removeMainToolBar(){Menus.removeMenu(Menus.AppMenuBar.FILE_MENU);Menus.removeMenu(Menus.AppMenuBar.EDIT_MENU);Menus.removeMenu(Menus.AppMenuBar.FIND_MENU);Menus.removeMenu(Menus.AppMenuBar.VIEW_MENU);Menus.removeMenu(Menus.AppMenuBar.NAVIGATE_MENU);Menus.removeMenu(Menus.AppMenuBar.HELP_MENU)}function removeRightSideToolBar(){Resizer.makeResizable("#main-toolbar");Resizer.hide("#main-toolbar");$(".content").css("right","0")}exports.hide=hide;exports.removeLeftSideToolBar=removeLeftSideToolBar;exports.removeMainToolBar=removeMainToolBar;exports.removeRightSideToolBar=removeRightSideToolBar});